const colorVertices = require('geometry/utils/colorVertices');

function IconCloseGeometry(params) {
	THREE.Geometry.call(this);

	var xChunkGeom = new THREE.PlaneGeometry(params.size, params.thickness);
	colorVertices(xChunkGeom, params.color);

	var xRotateMatrix = (new THREE.Matrix4()).makeRotationFromEuler(new THREE.Euler(0, 0, Math.PI * 0.25));
	this.merge(xChunkGeom, xRotateMatrix);
	
	xRotateMatrix = (new THREE.Matrix4()).makeRotationFromEuler(new THREE.Euler(0, 0, Math.PI * -0.25));
	this.merge(xChunkGeom, xRotateMatrix);
}

IconCloseGeometry.prototype = Object.create(THREE.Geometry.prototype);

module.exports = IconCloseGeometry;