const THREE = require('three');
function normalizeVertices(geom) {
	if(!geom.boundingBox){
		geom.computeBoundingBox();
	}
	var bounds = geom.boundingBox;
	var size = bounds.getSize();
	var center = bounds.getCenter();

	var translateMatrix = (new THREE.Matrix4()).makeTranslation(-center.x, -center.y, -center.z);
	var maxSize = Math.max(Math.max(size.x, size.y), size.z)
	var scaleMatrix = (new THREE.Matrix4()).makeScale(1/maxSize, 1/maxSize, 1/maxSize);

	geom.applyMatrix(translateMatrix);
	geom.applyMatrix(scaleMatrix);
	// debugger;
}

module.exports = normalizeVertices;