function hotwireUniforms(target, keys) {
	keys.forEach(function(key) {
		Object.defineProperty(
			target, 
			key, 
			{
				set: function (val) {
					if(this.uniforms[key].value.copy) {
						this.uniforms[key].value.copy(val);
					} else {
						this.uniforms[key].value = val;
					}
				},
				get: function() {
					return this.uniforms[key].value;
				}
			}
		)
	});
}

module.exports = hotwireUniforms;