var THREE = require('three');
var defaults = require('lodash.defaults');

var __defaultParams = {

};

var __geomtry;
function __getGeometry() {
	if(!__geomtry) {
		__geomtry = new THREE.SphereGeometry(0.5, 16, 8);
	}
	return __geomtry;
}

var __material;
function __getMaterial() {
	if(!__material) {
		__material = new THREE.MeshBasicMaterial({
			color: 0xffffff,
			wireframe: true
		});
	}
	return __material;
}

function TestSphere(params) {
	params = defaults(params, __defaultParams);

	var geometry = __getGeometry();
	var material = __getMaterial();

	THREE.Mesh.call(this, geometry, material);
}

TestSphere.prototype = Object.create(THREE.Mesh.prototype);

TestSphere.prototype.onEnterFrame = function() {
	this.rotation.y += 0.005;
}

module.exports = TestSphere;