function nope() {
	console.warn('Checking if VR too early. Wait till user decides.');
	return false;
}

var __checkMethod = nope;

function isVR() {
	return __checkMethod();
}

function setCheckMethod(checkMethod) {
	__checkMethod = checkMethod;
}

isVR.setCheckMethod = setCheckMethod;

module.exports = isVR;